import React, { useEffect } from "react";
import { Badge, Table } from "reactstrap";
import { useStore } from "zustand";

const HomeList = () => {
  const { currentCategory, homes, getHomes } = useStore((state) => ({
    currentCategory: state.currentCategory,
    homes: state.homes,
    getHomes: state.getHomes,
  }));

  useEffect(() => {
    getHomes();
  }, [getHomes]);

  return (
    <div>
      <h3>
        <Badge style={{ marginRight: "10px" }} color="warning">
          Homes
        </Badge>
        <Badge color="success">{currentCategory.categoryName}</Badge>
      </h3>

      <Table>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
          </tr>
        </thead>
        <tbody>
          {homes.map((homes) => (
            <tr>
              <th scope="row">{homes.id}</th>
              <th>{homes.productName}</th>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default HomeList;
