import React, { Component } from "react";
import { Badge, Row, Col } from "reactstrap";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import * as homeActions from "../../redux/actions/homeActions";
import "./HomeList.css";

class HomeList extends Component {
  componentDidMount() {
    this.props.actions.getHomes();
  }

  render() {
    return (
      <div>
        <h3>
          <Badge style={{ marginRight: "10px" }} color="warning">
            Homes
          </Badge>
          <Badge color="success">
            {this.props.currentCategory.categoryName}
          </Badge>
        </h3>

        <div className="content grid3 mtop">
          <Row>
            {this.props.homes.map((homes) => (
              <Col xs="6" key={homes.id} style={{ marginBottom: "1rem" }}>
                <div
                  style={{ display: "grid", gridTemplateColumns: "1fr, 1fr" }}
                >
                  <div className="box shadow" style={{ borderRadius: "10px" }}>
                    <div className="img" style={{ margin: "10px" }}>
                      <img
                        src={homes.image}
                        alt=""
                        style={{ borderRadius: "10px" }}
                      />
                    </div>
                    <div className="text">
                      <div className="category flex">
                        <span
                          style={{
                            background:
                              homes.categoryId === 1
                                ? "#25b5791a"
                                : "#ff98001a",
                            color:
                              homes.categoryId === 1 ? "#25b579" : "#ff9800",
                          }}
                        >
                          {homes.categoryId}
                        </span>
                        <i className="fa fa-heart"></i>
                      </div>
                      <h4 style={{ textAlign: "center" }}>{homes.name}</h4>
                      <p style={{ textAlign: "center" }}>
                        <i className="fa fa-location-dot"></i> {homes.location}
                      </p>
                    </div>
                    <div style={{ display: "-ms-flexbox", alignItems:"center"}}>
                      <div style={{ display: "block" }}>
                        <button className="btn2">{homes.price}</button>
                      </div>
                      <span >Type</span>
                    </div>
                  </div>
                </div>
              </Col>
            ))}
          </Row>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentCategory: state.changeCategoryReducer,
    homes: state.homeListReducer,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getHomes: bindActionCreators(homeActions.getHomes, dispatch),
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(HomeList);
