import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import * as tenantActions from "../../redux/actions/tenantActions";
import { Row, Col, Badge } from "reactstrap";

class TenantList extends Component {
  componentDidMount() {
    this.props.actions.getTenants();
  }

  render() {
    return (
      <div>
        <h3>
          <Badge color="warning">KiracÄ±lar</Badge>
        </h3>
        <div className="content grid3 mtop">
          <Row>
            {this.props.tenants.map((tenants) => (
              <Col xs="6" key={tenants.id} style={{ marginBottom: "1rem" }}>
                <div
                  style={{ display: "grid", gridTemplateColumns: "1fr, 1fr" }}
                >
                  <div className="box shadow" style={{ borderRadius: "10px" }}>
                    <div className="img" style={{ margin: "10px" }}>
                      <img
                        src={tenants.image}
                        alt=""
                        style={{ borderRadius: "10px" }}
                      />
                    </div>
                    <div className="text">
                      <div className="category flex"></div>
                      <h4 style={{ textAlign: "center" }}>{tenants.name}</h4>
                      <h4 style={{ textAlign: "center" }}>{tenants.surname}</h4>
                      <p style={{ textAlign: "center" }}>
                        <i className="fa fa-location-dot"></i> {tenants.info}
                      </p>
                    </div>
                    <div style={{ padding: "0.2rem 1rem 1rem 1rem" }}>
                      <button style={{ textAlign: "center" }} className="btn2">
                        {tenants.budget}
                      </button>
                      <button className="btn3">
                        <i
                          style={{ alignItems: "right" }}
                          className="fa fa-heart"
                        ></i>
                      </button>
                    </div>
                  </div>
                </div>
              </Col>
            ))}
          </Row>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    tenants: state.tenantListReducer,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getHomes: bindActionCreators(tenantActions.getTenants, dispatch),
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(TenantList);
