import * as actionTypes from "./actionTypes";

export function getHometownSuccess(hometown) {
  return { type: actionTypes.GET_HOMETOWN_SUCCESS, payload: hometown };
}

export function getHometowns(cityId) {
  return function (dispatch) {
    let url = "http://localhost:3001/cities";
    if (cityId) {
      url = url + "?categoryId=" + cityId;
    }
    return fetch(url)
      .then((response) => response.json())
      .then((result) => dispatch(getHometownSuccess(result)));
  };
}
